//Get the target asset sys_id from the scratchpad.
var assetID = workflow.scratchpad.assetToAssignID;
//Get the target asset record from scratchpad ID.
var assetRecord = new GlideRecord('alm_asset');
assetRecord.get(assetID);

//Get the stockroom to pull asset from. 
var fromStock = assetRecord.stockroom.getRefRecord();
var fromStockID = fromStock.sys_id;
//Get the stockroom to push asset to.
var toStockID = workflow.scratchpad.targetStockroomID;
var toStock = new GlideRecord('alm_stockroom');
toStock.get(toStockID);
//Define a delivery date that is 5 days in the future.
var deliveryDate = new GlideDateTime();
deliveryDate.addDaysLocalTime(5);
//Create the transfer order record.
var trOrder = new GlideRecord('alm_transfer_order');
trOrder.initialize();
trOrder.setValue('from_stockroom', fromStockID);
trOrder.setValue('to_stockroom', toStockID);
trOrder.setValue('from_location', fromStock.location.sys_id);
trOrder.setValue('to_location', toStock.location.sys_id);
trOrder.setValue('delivery_by_date', deliveryDate);
trOrder.insert();
//Create the transfer order line record.
var trOrLine = new GlideRecord('alm_transfer_order_line');
trOrLine.initialize();
trOrLine.setValue('from_stockroom', fromStockID);
trOrLine.setValue('to_stockroom', toStockID);
trOrLine.setValue('model', assetRecord.model.sys_id);
trOrLine.setValue('asset', assetRecord.sys_id);
trOrLine.setValue('transfer_order', trOrder.sys_id);
trOrLine.insert();
//Store transfer order sys_id in scratchpad variable.
workflow.scratchpad.transferOrderID = trOrder.getValue('sys_id');

