//Obtain the requested item record.
var reqItemRecord = new GlideRecord('sc_req_item');
reqItemRecord.get(current.sys_id);
//Get record of item model.
var modelRecord = reqItemRecord.cat_item.model.getRefRecord();
//Get an asset of that model that is also 'in stock'.
var assetRecord = new GlideRecord('alm_asset');
assetRecord.addQuery('model', modelRecord.sys_id);
assetRecord.addQuery('install_status', 6);
assetRecord.query();
//If an asset record is available for assignment, return 'yes'
//and send record sys_id to scratchpad.
if(assetRecord.next()) {
	workflow.scratchpad.assetToAssignID = assetRecord.sys_id;
	answer = 'yes';
}
//If no record is available for assignment, return 'no'.
else {
	answer = 'no';
}

