var user = new GlideRecord('sys_user');
user.get(current.requested_for);
var userLocation = user.building.location,
	uLat = userLocation.latitude,
	uLong = userLocation.longitude,
	
	stockroom = new GlideRecord('alm_stockroom');
stockroom.addQuery('type','IN','aeaa2b3f3763100044e0bfc8bcbe5dde,a2aa2b3f3763100044e0bfc8bcbe5de0');
stockroom.query();

var closest = {
	sys_id:'',
	distance:Number.MAX_VALUE
};

while(stockroom.next()) {
	var thisDist = calculateDistance(uLat, uLong, stockroom.location.latitude, stockroom.location.longitude);
	if(thisDist < closest.distance) {
		closest.sys_id = stockroom.getValue('sys_id');
		closest.distance = thisDist;
	}
}

workflow.scratchpad.targetStockroomID = closest.sys_id;

function calculateDistance(targLat, targLon, stockLat, stockLon) {
	var RADIUS = 6378.16,
		dLat = degreeToRadian(stockLat-targLat),
		dLon = degreeToRadian(stockLon-targLon),
		a = 
			Math.sin(dLat/2) * Math.sin(dLat/2) +
			Math.cos(degreeToRadian(targLat)) * Math.cos(degreeToRadian(stockLat)) * 
			Math.sin(dLon/2) * Math.sin(dLon/2), 
		c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a)),
		d = RADIUS * c;
  return d;
}

function degreeToRadian(degree) {
	return degree * (Math.PI/180);
}

